import React from 'react'

//Formulario para una escuela
//Formulario para dar de alta a profesores y alumnos nombre (cantidad en cada caso)
const Form = () => {

    const [nombre, setNombre] = React.useState('');
    const [state, setState] = React.useState({

        ...state,
        profesores: 0,
        alumnos: 0
    });

    const editarNombre = (event) => setNombre(event.target.value);
    const editarProfesores = (event) => setState({ profesores: event.target.value });
    const editarAlumnos = (event) => setState({ alumnos: event.target.value });

    return (
        <div className='margen'>
            {/*Nombre */}
            <input onChange={editarNombre} />
            {nombre}
            <br />
            {/*Numero de profesores */}
            <input onChange={editarProfesores} />
            {state.profesores}
            <br />
            {/*Numero de Alumnos */}
            <input onChange={editarAlumnos} />
            {state.alumnos}
        </div>
    )
}
export default Form;
