import React, { Component } from "react"

class ClockUsingClass extends Component {

    //Ciclo de vida
    constructor(props){
        super(props);
        //Estado inicial
        this.state = {
            date: new Date(),
        };
    }

    render() {
        return (
            <div>
                Hola Mundo desde compoente de clase
            </div>            
        )
    }
}

export default ClockUsingClass;
